import React, { FC } from "react"
import { Controller, SubmitHandler, useForm } from 'react-hook-form'
import { UserTypes } from "../Types/userForm.interface"
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { Grid, Input, Stack, Typography } from "@mui/material";
import UserStore from "../Store/user-store";
import { observer } from "mobx-react-lite";



const UserForm = observer(() => {

  const {
    register,
    handleSubmit,
    watch,
    reset,
    setValue,
    formState: { errors },
    control, } = useForm<UserTypes>({})


  const onSubmit: SubmitHandler<UserTypes> = (data) => {
    UserStore.addUser(data)
    reset()
  }

  const EditUser = () => {

  }

  return (
    <>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Stack
          direction="column"
          justifyContent="center"
          alignItems="stretch"
          spacing={2}
          maxWidth='800px'
          minHeight='360px'
          margin='20px auto'
        >
          <Controller
            name="name"
            control={control}
            render={({field, }) => (
              <>
              <TextField {...field} type="text" placeholder='Name' id="name" label="Your name" variant="outlined" />
              {error && <div style={{ color: 'red' }}>{error.message}</div>}
              </>
              )}
          />
          {/* <TextField {...register('name', { required: '* Name is required' })} type="text" placeholder='Name' id="name" label="Your name" variant="outlined" />
          {errors?.name && (<div style={{ color: 'red' }}>{errors.name.message}</div>)} */}

          <TextField {...register('username', { required: '* Username is required' })} type="text" placeholder='UserName' id="username" label=" Username" variant="outlined" />
          {errors?.username && (<div style={{ color: 'red' }}>{errors.username.message}</div>)}

          <TextField {...register('phone')} type="text" placeholder='phone' id="phone" label=" phone" variant="outlined" />
          {errors?.phone && (<div style={{ color: 'red' }}>{errors.phone.message}</div>)}

          <TextField {...register('email', {
            pattern: {
              value: /([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/,
              message: '*Enter valid email'
            }
          })} type="text" placeholder='Email' variant="outlined" label="email" />
          {errors?.email && (<div style={{ color: 'red' }}>{errors.email.message}</div>)}

          <Button variant="contained" type="submit">Send</Button>
        </Stack>
      </form>

    </>
  )
})

export default UserForm;
