import { action, computed, makeAutoObservable, makeObservable, observable, observe } from 'mobx'

interface Inputs {
    nameInput: string;
    usernameInput: string;
    emailInput: string;
    phoneInput: string;

}

class UserStore {
    user = {
        name:'',
        username:'',
        phone:'',
        email:'',
    }

    constructor() {
        makeAutoObservable(this)
    }
    createUser = ({nameInput, usernameInput,phoneInput,emailInput}:Inputs) => {
        this.user.name = nameInput
        this.user.username = usernameInput
        this.user.phone = phoneInput
       this.user.email = emailInput
    }
}

export default UserStore;
