import { action, computed, makeAutoObservable, observable, observe } from 'mobx'
import { SubmitHandler } from 'react-hook-form'
import User from '../Components/DeleteUser'
import { UserTypes } from "../Types/userForm.interface"



class UsersStore {
    isLoading = false
    users: any[] = []
    query:string=''


    constructor() {
        makeAutoObservable(this)
    }


    addUser(user: object) {
        this.users.push(user)
    }

    removeUser(username: string) {
        this.users = this.users.filter((user: any) => user.username !== username)
    }

    filterUser(query: string) {
        if (!query) {
            return this.users
        }
         this.users.filter(user => user.username.toLowerCase().includes(query.toLowerCase()))

    }

    fetchUsers() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json =>
                this.users = [(this.users.length ? this.users : json), ...json]
            )
    }
}

export default new UsersStore();
